$(document).ready((function(){$(".main-slider").slick({arrows:!1,autoplay:!0,autoplaySpeed:7e3,speed:200,draggable:!1,touchThreshold:100}),$(".reviews").slick({slidesToShow:3,initialSlide:3,waitForAnimate:!1,touchThreshold:100,responsive:[{breakpoint:1060,settings:{slidesToShow:2}},{breakpoint:720,settings:{slidesToShow:1}}]})})),window.onload=function(){const e=document.querySelector(".js-navigation-toggle"),o=document.querySelector(".header__navigation");e.addEventListener("click",(function(){o.classList.toggle("nav-toggle-close"),o.classList.contains("nav-toggle-close")?(o.style.display="flex",e.style.opacity="0"):(o.style.display="none",e.style.opacity="1")}))};